import axios from "axios";
import { useState } from "react";
import useInput from "../hooks/useInputs";
import { useNavigate } from "react-router-dom";

const NewUser = () => {
  const nameHook = useInput();
  const navigate = useNavigate();
  // const lastName = useInput()
  // const age = useInput()


  const [lastName, setLastName] = useState("");
  const [age, setAge] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("http://localhost:3001/api", {
        name: nameHook.value,
        lastName,
        age,
      })
      .then(() => alert("UserCreated"))
      .then(() => navigate("/users"))
      .catch((err) => console.log(err));
  };

  // const handleNameChange = (e) => {
  //   setName(e.target.value);
  // };
  const handleLastNameChange = (e) => {
    setLastName(e.target.value);
  };
  const handleAgeChange = (e) => {
    setAge(e.target.value);
  };

  return (
    <div className="layout m-5">
      <h3 className="title is-3">New User</h3>
      <form onSubmit={handleSubmit}>
        <label className="label my-3">Usuario</label>
        <input
          // value={nameHook.value}
          // onChange={nameHook.onChange}
          {...nameHook}
          // onChange={handleNameChange}
          className="input my-3"
          type="text"
          placeholder="Usuario"
        />

        <label className="label my-3">Email</label>
        <input
          onChange={handleLastNameChange}
          className="input my-3"
          type="email"
          placeholder="LastName"
        />

        <label className="label my-3">Contra</label>
        <input
          onChange={handleAgeChange}
          className="input my-3"
          type="number"
          placeholder="Age"
        />
        <button className="button is-link my-5">Submit</button>
      </form>
    </div>
  );
};

export default NewUser;
