import axios from "axios";
import { useNavigate } from "react-router-dom";
import { useState } from "react";

const NewUser = () => {
  const navigate= useNavigate()
  const [user, setUser] = useState({...user, email: '', userName:'', password:''})

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("http://localhost:3001/api/register", user)
      .then(() => navigate("/users"))
      .catch((err) => console.log(err));
  };

  return (
    <div className="layout m-5">
      <h3 className="title is-3">Crea tu usuario</h3>
      <form onSubmit={handleSubmit}>
        <label className="label my-3">Usuario</label>
        <input
          onChange={(e)=> setUser({...user, userName:e.target.value})}
          className="input my-3"
          type="text"
          placeholder="Usuario"
        />

        <label className="label my-3">Email</label>
        <input
        onChange={(e)=> setUser({...user, email:e.target.value})}
          className="input my-3"
          type="email"
          placeholder="Email"
        />

        <label className="label my-3">Contraseña</label>
        <input
           onChange={(e)=> setUser({...user, password:e.target.value})}
          className="input my-3"
          type="password"
          placeholder="Contraseña"
        />
        <button className="button is-link my-5">Submit</button>
      </form>
    </div>
  );
};

export default NewUser;
