import { Route, Routes } from "react-router";
import Navbar from "./components/nadvar";
import Grids from "./components/Grid";
import { useEffect, useState } from "react";
import axios from "axios";
import NewUser from "./components/NewUser";
import Register from "./components/Register";
import Search from "./components/Search";
import { AuthContext } from './context/AuthContext'
import { useContext } from "react";

const App = () => {
  const {toggleAuth}= useContext(AuthContext)
  const [movies, setMovies] = useState([]);
  const [tv, setTv] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:3001/api/peliculas")
      .then((res) => res.data)
      .then((peliculas) => setMovies(peliculas));

    axios
      .get("http://localhost:3001/api/series")
      .then((res) => res.data)
      .then((serie) => setTv(serie));

      axios.get('login/me')
    .then(res=>res.data)
    .then(user=>toggleAuth(user))

  }, []);

  return (
    <div>
      <Navbar />
      <Routes>

        <Route path="/search" element={<Search />} />
        <Route path="/search/*" element={<Search />} />
        <Route path="/:type" element={<Grids movies={movies} tv={tv} />} />
        <Route path="/newuser" element={<NewUser />} />
        <Route path="/login" element={<Register />} />
      </Routes>
    </div>
  );
};

export default App;
