import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const Card = ({ type, id }) => {
  const [media, setMedia] = useState();




console.log('MEDIA',media)

  useEffect(async () => {
    const carMedia= await axios.post("http://localhost:3001/search/single", { id, type })
      .then((res) => setMedia(res));
  }, []);  




  return (
    <>
       <div class="card">
       {/*  <img src= {imageUrl}  />   */}
        <div class="card-content">
           <h2> {media.data.title}  </h2>
          <p>  {media.data.overview}  </p> 
           <Link to={`/simpleview/${id}/${type}`}> 
            <a href="#" class="btn">
              {" "}
              See more information
              <i class="las la-long-arrow-alt-right"></i>
            </a>
           </Link>  
        </div>
      </div> 
    </>
  );
};

export default Card;
