import axios from "axios";
import useInput from "../hooks/useInputs";
import { useNavigate } from "react-router-dom";

const NewUser = () => {
  const userHook = useInput();
  const emailHook = useInput();
  const passwordHook = useInput();
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("http://localhost:3001/api/register", {
        user: userHook.value,
        email: emailHook.value,
        password: passwordHook.value,
      })
      .then(() => navigate("/users"))
      .catch((err) => console.log(err));
  };

  return (
    <div className="layout m-5">
      <h3 className="title is-3">Crea tu usuario</h3>
      <form onSubmit={handleSubmit}>
        <label className="label my-3">Usuario</label>
        <input
          {...userHook}
          className="input my-3"
          type="text"
          placeholder="Usuario"
        />

        <label className="label my-3">Email</label>
        <input
          {...emailHook}
          className="input my-3"
          type="email"
          placeholder="Email"
        />

        <label className="label my-3">Contraseña</label>
        <input
          {...passwordHook}
          className="input my-3"
          type="password"
          placeholder="Contraseña"
        />
        <button className="button is-link my-5">Submit</button>
      </form>
    </div>
  );
};

export default NewUser;
