import { Route, Routes} from 'react-router';
import Navbar from './components/nadvar'
import Search from './components/Search';
import Grids from './components/Grid';
import { useEffect, useState } from 'react';
import axios from 'axios';


const App = ()=>{

    const [movies, setMovies] = useState([])

    useEffect(()=>{
        axios.get('http://localhost:3001/api/peliculas')
        .then(res => res.data)
        .then(peliculas => { console.log(peliculas)
            return setMovies(peliculas)})


    },[])

    const [tv, setTv] = useState([])

    useEffect(()=>{
        axios.get('http://localhost:3001/api/series')
        .then(res => res.data)
        .then(serie => setTv(serie))
    },[])




    return (
        <div>
          <Navbar />

          <Routes>
              <Route path="/search" element={<Search />}/>
              <Route path="/:type" element={< Grids movies={movies} tv={tv}/>}/>

          </Routes>


          </div>
    )}

export default App;
