import axios from "axios";
import { useState } from "react";
import { AuthContext } from '../context/AuthContext'
import { useContext } from "react";
//import { useNavigate } from "react-router-dom";

const Register= () => {

const {toggleAuth}=useContext(AuthContext)
const [userEmail, setUserEmail]= useState('')
const [userPassword, setUserPassword]= useState('')
//const navigate = useNavigate();

const handleSubmit= (e)=>{
  e.preventDefault();
  axios.post('api/login',{
    email: userEmail,
    password: userPassword

  }).then(data=> toggleAuth())
 // .then(() => navigate("/"))
}

  return (
    <div className="layout m-5">
      <h3 className="title is-3">Iniciar sesion</h3>

      <form onSubmit={handleSubmit} >
        <label className="label my-3">Email</label>
        <input onChange={(e)=> setUserEmail(e.target.value)} className="input my-3" type="email" placeholder="Email" />

        <label className="label my-3">Contraseña</label>
        <input
        onChange={(e)=> setUserPassword(e.target.value)}
          className="input my-3"
          type="password"
          placeholder="Contraseña"
        />
        <button className="button is-link my-5">Submit</button>
      </form>
    </div>
  );
};

export default Register;
