import axios from "axios";
import { useState, useEffect } from "react";
import GridS from "./GridS";



const Search = () => {
  const apiKey= '0ed87d57b84f7c9d6c2300276bae6c03';
  const [search, setSearch] = useState("");
  const [searchMovie, setSearchMovie] = useState([]);
  const searchChange = (e) => {
    setSearch(e.target.value);
  };
  useEffect(()=>{
    axios
    .get(`https://api.themoviedb.org/3/search/movie?api_key=${apiKey}&query=${search}`)
    .then(movies => setSearchMovie)
  })

  return (
    <div className="field" id="searchform">
      <input
        value={search}
        onChange={searchChange}
        type="text"
        id="searchterm"
        placeholder="Â¿Que pelicula desea mirar?"
      />
      <button type="button" id="search">
        Buscar
      </button>
      <div>

      {search ? <GridS searchMovies={searchMovie} /> : <></>}
      </div>
    </div>
  );
};

export default Search;
